mapper.update:
UPDATE article
SET title = #{title},
    contents = #{contents}
WHERE idx = #{idx}

mapper.insert:
INSERT
INTO article (
user_idx
, title
, contents
)
VALUES (
#{userIdx}
, #{title}
, #{contents}
)

    SELECT LAST_INSERT_ID()

mapper.update:
UPDATE article
SET del_yn = 'Y'
WHERE idx = #{idx}

mapper.insert:
INSERT
INTO articleLike (
user_idx
, article_idx
)
VALUES (
#{userIdx}
, #{articleIdx}
)

mapper.delete:
DELETE FROM articleLike
WHERE  user_idx = #{userIdx}
AND article_idx = #{articleIdx}

mapper.select:
SELECT count(*) FROM articleLike
WHERE user_idx = #{userIdx}
AND article_idx = #{articleIdx}
